import React, { useState, useEffect } from 'react';
import api from './services/api';
import './global.css';
import './App.css';
import './Sidebar.css';
import './Main.css';

//Componente: Bloco isolado de HTML, CSS e JS, o qual não interfere no restante da aplicação
//Propriedade: Informações que um componente PAI passa para o componente FILHO
//Estado: Informações mantidas pelo componente (lembrar: imutabilidade)

function App() {
	const [github_username, setGithub_username] = useState('');
	const [techs, setTechs] = useState('');
	const [latitude, setLatitude] = useState('');
	const [longitude, setLongitude] = useState('');

	useEffect(() => {
		navigator.geolocation.getCurrentPosition(
			(position) => {
				const {latitude, longitude} = position.coords;

				setLatitude(latitude);
				setLongitude(longitude);
			},
			(err) => {
				console.log(err);
			},
			{
				timeout: 30000,
			}
		);
	}, []);

	async function handleAddDev(e) {
		e.preventDefault();

		const response = await api.post('/devs', {
			github_username,
			techs,
			latitude,
			longitude,
		});

		console.log(response.data);
	}

	return (
		<div id="app">
			<aside>
				<strong>Cadastrar</strong>
				<form onSubmit={handleAddDev}>
					<div className="input-block">
						<label htmlFor="github_username">Usuário do Github</label>
						<input name="github_username" 
							id="github_username" 
							required
							value={github_username}
							onChange={e => setLatitude(e.target.value)}
						/>
					</div>

					<div className="input-block">
						<label htmlFor="techs">Tecnologias</label>
						<input name="techs" 
							id="techs" 
							required
							value={techs}
							onChange={e => setLatitude(e.target.value)}
						/>
					</div>

					<div className="input-group">
						<div className="input-block">
							<label htmlFor="latitude">Latitude</label>
							<input type="number"
								name="latitude" 
								id="latitude" 
								required 
								value={latitude}
								onChange={e => setLatitude(e.target.value)}
							/>
						</div>

						<div className="input-block">
							<label htmlFor="longitude">Longitude</label>
							<input type="number" 
								name="longitude" 
								id="longitude" 
								required 
								value={longitude}
								onChange={e => setLongitude(e.target.value)}
							/>
						</div>
					</div>

					<button type="submit">Salvar</button>
				</form>
			</aside>
			<main>
				<ul>
					<li className="dev-item">
						<header>
							<img src="https://avatars3.githubusercontent.com/u/51677502?s=400&u=974f9d9766e78f3aa38a791b8fad1c2c1d4ecd8c&v=4" alt="Rafael Rocha"/>
							<div className="user-info">
								<strong>Rafael Rocha</strong>
								<span>ReactJS, React Native, Node.js</span>
							</div>
						</header>
						<p>Computer Science student. 21 year old. Interested in web development.</p>
						<a href="https://github.com/dell2duo">Acessar perfil no Github</a>
					</li>
					<li className="dev-item">
						<header>
							<img src="https://avatars3.githubusercontent.com/u/51677502?s=400&u=974f9d9766e78f3aa38a791b8fad1c2c1d4ecd8c&v=4" alt="Rafael Rocha"/>
							<div className="user-info">
								<strong>Rafael Rocha</strong>
								<span>ReactJS, React Native, Node.js</span>
							</div>
						</header>
						<p>Computer Science student. 21 year old. Interested in web development.</p>
						<a href="https://github.com/dell2duo">Acessar perfil no Github</a>
					</li>
					<li className="dev-item">
						<header>
							<img src="https://avatars3.githubusercontent.com/u/51677502?s=400&u=974f9d9766e78f3aa38a791b8fad1c2c1d4ecd8c&v=4" alt="Rafael Rocha"/>
							<div className="user-info">
								<strong>Rafael Rocha</strong>
								<span>ReactJS, React Native, Node.js</span>
							</div>
						</header>
						<p>Computer Science student. 21 year old. Interested in web development.</p>
						<a href="https://github.com/dell2duo">Acessar perfil no Github</a>
					</li>
					<li className="dev-item">
						<header>
							<img src="https://avatars3.githubusercontent.com/u/51677502?s=400&u=974f9d9766e78f3aa38a791b8fad1c2c1d4ecd8c&v=4" alt="Rafael Rocha"/>
							<div className="user-info">
								<strong>Rafael Rocha</strong>
								<span>ReactJS, React Native, Node.js</span>
							</div>
						</header>
						<p>Computer Science student. 21 year old. Interested in web development.</p>
						<a href="https://github.com/dell2duo">Acessar perfil no Github</a>
					</li>
				</ul>
			</main>
		</div>
	);
}

export default App;
